// src/components/ClubDashboard.js
import React, { useState } from 'react';
import CreateEventForm from './CreateEventForm';

function ClubDashboard({ events, onCreateEvent, onLinkFollower, onNotifyFollowers }) {
  const [isCreateEventOpen, setCreateEventOpen] = useState(false);
  const [isLinkFollowerOpen, setLinkFollowerOpen] = useState(false);
  const [isNotifyFollowersOpen, setNotifyFollowersOpen] = useState(false);
  const [isConsultarBoletasOpen, setConsultarBoletasOpen] = useState(false); // Estado para controlar la apertura de la sección de boletas

  const [isCrearServicioOpen, setCrearServicioOpen] = useState(false); // Estado para controlar la apertura del formulario de crear servicio adicional
  const [servicioNombre, setServicioNombre] = useState('');
  const [servicioPrecio, setServicioPrecio] = useState('');
  const [servicioDescripcion, setServicioDescripcion] = useState('');

  const handleToggleCreateEvent = () => {
    setCreateEventOpen(!isCreateEventOpen);
    setLinkFollowerOpen(false);
    setNotifyFollowersOpen(false);
    setConsultarBoletasOpen(false); // Cerrar la sección de boletas al abrir otro formulario
    setCrearServicioOpen(false); // Cerrar el formulario de crear servicio adicional al abrir otro formulario
  };

  const handleToggleLinkFollower = () => {
    setLinkFollowerOpen(!isLinkFollowerOpen);
    setCreateEventOpen(false);
    setNotifyFollowersOpen(false);
    setConsultarBoletasOpen(false); // Cerrar la sección de boletas al abrir otro formulario
    setCrearServicioOpen(false); // Cerrar el formulario de crear servicio adicional al abrir otro formulario
  };

  const handleToggleNotifyFollowers = () => {
    setNotifyFollowersOpen(!isNotifyFollowersOpen);
    setCreateEventOpen(false);
    setLinkFollowerOpen(false);
    setConsultarBoletasOpen(false); // Cerrar la sección de boletas al abrir otro formulario
    setCrearServicioOpen(false); // Cerrar el formulario de crear servicio adicional al abrir otro formulario
  };

  const handleConsultarBoletas = (event) => {
    // Lógica para consultar boletas vendidas del evento
    console.log(`Consultar boletas vendidas para el evento ${event.name}`);
    setConsultarBoletasOpen(prevState => !prevState); // Alternar el estado de la sección de boletas
    setCrearServicioOpen(false); // Cerrar el formulario de crear servicio adicional al consultar boletas
  };

  const handleCreateService = (event) => {
    // Lógica para crear servicio adicional para el evento
    console.log(`Crear servicio adicional para el evento ${event.name}`);
    setCrearServicioOpen(prevState => !prevState); // Alternar el estado de la apertura del formulario de crear servicio adicional
    setConsultarBoletasOpen(false); // Cerrar la sección de boletas al abrir el formulario de crear servicio adicional
  };

  const handleCreateServiceSubmit = (event) => {
    event.preventDefault();
    // Aquí puedes realizar la lógica para enviar los datos del servicio adicional al backend o almacenarlos
    console.log(`Nombre: ${servicioNombre}, Precio: ${servicioPrecio}, Descripción: ${servicioDescripcion}`);
    // Puedes agregar más lógica según tus requerimientos, como enviar los datos al backend

    // Limpiar los campos después de enviar el formulario
    setServicioNombre('');
    setServicioPrecio('');
    setServicioDescripcion('');
    setCrearServicioOpen(false); // Cerrar el formulario de crear servicio adicional después de enviar
  };

  return (
    <div className="dashboard">
      <h1>Club Dashboard</h1>

      {/* Botones y formularios */}
      <div>
        <button onClick={handleToggleCreateEvent}>Crear Evento Deportivo</button>
        {isCreateEventOpen && (
          <div className="form">
            <h2>Crear Evento Deportivo</h2>
            <CreateEventForm onCreateEvent={(formData) => {
              handleCreateEventSubmit(formData);
            }} />
          </div>
        )}
      </div>
      <div>
        <button onClick={handleToggleLinkFollower}>Vincular Seguidor</button>
        {isLinkFollowerOpen && (
          <div className="form">
            <h2>Vincular Seguidor</h2>
            <form onSubmit={onLinkFollower}>
              <input type="email" placeholder="Correo electrónico" required />
              <button type="submit">Vincular Seguidor</button>
            </form>
          </div>
        )}
      </div>
      <div>
        <button onClick={handleToggleNotifyFollowers}>Notificar Seguidores</button>
        {isNotifyFollowersOpen && (
          <div className="form">
            <h2>Notificar Seguidores</h2>
            <form onSubmit={onNotifyFollowers}>
              <textarea placeholder="Mensaje a enviar" required></textarea>
              <button type="submit">Enviar Mensaje</button>
            </form>
          </div>
        )}
      </div>

      {/* Lista de eventos */}
      <div className="event-list">
        <h2>Eventos Creados</h2>
        <ul>
          {events.map((event) => (
            <li key={event.id}>
              <strong>Nombre:</strong> {event.name} <br />
              <strong>Fecha:</strong> {event.date} <br />
              <strong>Ubicación:</strong> {event.location} <br />
              {/* Botones para consultar boletas vendidas y crear servicio adicional */}
              <button onClick={() => handleConsultarBoletas(event)}>Consultar Boletas Vendidas</button>
              <button onClick={() => handleCrearServicio(event)}>Crear Servicio Adicional</button>
              {/* Mostrar localidades y boletas vendidas */}
              {isConsultarBoletasOpen && event.localities && (
                <div>
                  <h3>Localidades y Boletas Vendidas:</h3>
                  <ul>
                    {event.localities.map(localidad => (
                      <li key={localidad.id}>
                        <strong>Nombre:</strong> {localidad.name} <br />
                        <strong>Capacidad:</strong> {localidad.capacity} <br />
                        <strong>Boletas Vendidas:</strong> {localidad.boletasVendidas} <br />
                      </li>
                    ))}
                  </ul>
                </div>
              )}
              {/* Formulario para crear servicio adicional */}
              {isCrearServicioOpen && (
                <div className="form">
                  <h2>Crear Servicio Adicional</h2>
                  <form onSubmit={handleCrearServicioSubmit}>
                    <input
                      type="text"
                      placeholder="Nombre del Servicio"
                      value={servicioNombre}
                      onChange={(e) => setServicioNombre(e.target.value)}
                      required
                    />
                    <input
                      type="number"
                      placeholder="Precio"
                      value={servicioPrecio}
                      onChange={(e) => setServicioPrecio(e.target.value)}
                      required
                    />
                    <textarea
                      placeholder="Descripción"
                      value={servicioDescripcion}
                      onChange={(e) => setServicioDescripcion(e.target.value)}
                      required
                    ></textarea>
                    <button type="submit">Crear Servicio</button>
                  </form>
                </div>
              )}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default ClubDashboard;
