// src/App.js
import React, { useState } from 'react';
import LoginForm from './components/LoginForm';
import CreateEventForm from './components/CreateEventForm'; // Importar el nuevo formulario
import './App.css';

function App() {
  const [loggedIn, setLoggedIn] = useState(false);
  const [userType, setUserType] = useState('');
  const [events, setEvents] = useState([]);

  const handleLogin = (username, password, userType) => {
    // Simulación de autenticación
    setLoggedIn(true);
    setUserType(userType);
  };

  const handleCreateEvent = (newEvent) => {
    // Agregar el nuevo evento al estado de eventos
    setEvents([...events, newEvent]);
    // Puedes agregar aquí la lógica para guardar el evento en tu backend si lo tienes
    console.log('Nuevo evento creado:', newEvent);
  };

  return (
    <div className="App">
      {!loggedIn ? (
        <LoginForm handleLogin={handleLogin} />
      ) : (
        <div>
          <h1>Bienvenido {userType === 'club' ? 'Club' : 'Seguidor'}</h1>
          {userType === 'club' && (
            <div>
              <CreateEventForm onCreateEvent={handleCreateEvent} />
              {/* Aquí puedes mostrar los eventos creados */}
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
