// src/components/ClubDashboard.js
import React, { useState } from 'react';
import CreateEventForm from './CreateEventForm';

function ClubDashboard({ events, onCreateEvent, onLinkFollower, onNotifyFollowers }) {
  const [isCreateEventOpen, setCreateEventOpen] = useState(false);
  const [isLinkFollowerOpen, setLinkFollowerOpen] = useState(false);
  const [isNotifyFollowersOpen, setNotifyFollowersOpen] = useState(false);

  const handleToggleCreateEvent = () => {
    setCreateEventOpen(!isCreateEventOpen);
    setLinkFollowerOpen(false);
    setNotifyFollowersOpen(false);
  };

  const handleToggleLinkFollower = () => {
    setLinkFollowerOpen(!isLinkFollowerOpen);
    setCreateEventOpen(false);
    setNotifyFollowersOpen(false);
  };

  const handleToggleNotifyFollowers = () => {
    setNotifyFollowersOpen(!isNotifyFollowersOpen);
    setCreateEventOpen(false);
    setLinkFollowerOpen(false);
  };

  const handleConsultarBoletas = (event) => {
    // Suponiendo que cada evento tiene un arreglo de localidades con nombre, capacidad y boletasVendidas
    const localidades = event.localidades.map(localidad => ({
      nombre: localidad.nombre,
      capacidad: localidad.capacidad,
      boletasVendidas: localidad.boletasVendidas || 0, // Inicia en cero si no hay datos
    }));

    // Mostrar en consola las localidades con su capacidad y boletas vendidas
    localidades.forEach(localidad => {
      console.log(`Localidad: ${localidad.nombre}, Capacidad: ${localidad.capacidad}, Boletas Vendidas: ${localidad.boletasVendidas}`);
    });
  };

  const handleCrearServicio = (event) => {
    // Lógica para crear servicio adicional para el evento
    console.log(`Crear servicio adicional para el evento ${event.name}`);
  };

  const handleCreateEventSubmit = (formData) => {
    onCreateEvent(formData); // Llama a la función para crear el evento

    // Cierra el formulario después de crear el evento
    setCreateEventOpen(false);
  };

  return (
    <div className="dashboard">
      <h1>Club Dashboard</h1>

      {/* Botones y formularios */}
      <div>
        <button onClick={handleToggleCreateEvent}>Crear Evento Deportivo</button>
        {isCreateEventOpen && (
          <div className="form">
            <h2>Crear Evento Deportivo</h2>
            <CreateEventForm onSubmit={handleCreateEventSubmit} />
          </div>
        )}
      </div>
      <div>
        <button onClick={handleToggleLinkFollower}>Vincular Seguidor</button>
        {isLinkFollowerOpen && (
          <div className="form">
            <h2>Vincular Seguidor</h2>
            <form onSubmit={onLinkFollower}>
              <input type="email" placeholder="Correo electrónico" required />
              <button type="submit">Vincular Seguidor</button>
            </form>
          </div>
        )}
      </div>
      <div>
        <button onClick={handleToggleNotifyFollowers}>Notificar Seguidores</button>
        {isNotifyFollowersOpen && (
          <div className="form">
            <h2>Notificar Seguidores</h2>
            <form onSubmit={onNotifyFollowers}>
              <textarea placeholder="Mensaje a enviar" required></textarea>
              <button type="submit">Enviar Mensaje</button>
            </form>
          </div>
        )}
      </div>

      {/* Lista de eventos */}
      <div className="event-list">
        <h2>Eventos Creados</h2>
        <ul>
          {events.map((event) => (
            <li key={event.id}>
              <strong>Nombre:</strong> {event.name} <br />
              <strong>Fecha:</strong> {event.date} <br />
              <strong>Ubicación:</strong> {event.location} <br />
              {/* Botón para consultar boletas vendidas */}
              <button onClick={() => handleConsultarBoletas(event)}>Consultar Boletas Vendidas</button>
              {/* Botón para crear servicio adicional */}
              <button onClick={() => handleCrearServicio(event)}>Crear Servicio Adicional</button>
              {/* Mostrar la lista de localidades */}
              <ul>
                {event.localidades.map((localidad, index) => (
                  <li key={index}>
                    <strong>Localidad:</strong> {localidad.nombre} <br />
                    <strong>Capacidad:</strong> {localidad.capacidad} <br />
                    <strong>Boletas Vendidas:</strong> {localidad.boletasVendidas || 0} <br />
                  </li>
                ))}
              </ul>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default ClubDashboard;
