// src/components/CreateEventForm.js
import React, { useState } from 'react';
import APIInvoke from '../utils/APIInvoke';
import swal from 'sweetalert';

function CreateEventForm({ onCreateEvent }) {

  const [localities, setLocalities] = useState([]);
  const [localityName, setLocalityName] = useState('');
  const [localityCapacity, setLocalityCapacity] = useState('');

  const handleAddLocality = () => {
    if (!localityName || !localityCapacity) {
      alert('Por favor, completa todos los campos de la localidad.');
      return;
    }
    setLocalities([...localities, { name: localityName, capacity: parseInt(localityCapacity) }]);
    setLocalityName('');
    setLocalityCapacity('');
  };

  const [eventoNuevo, setEventoNuevo] = useState({
    oponente: "",
    estadio: "",
    fecha: "",
    hora_ingreso: "",
    hora_cierre: "",
  });

  const { oponente, estadio, fecha, hora_ingreso, hora_cierre } = eventoNuevo;

  const setEvento = (e) => {
    setEventoNuevo({
      ...eventoNuevo,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Logica para enviar el evento

    try {

      const data = {
        "oponente": eventoNuevo.oponente,
        "estadio": eventoNuevo.estadio,
        "fecha": eventoNuevo.fecha,
        "hora_ingreso": eventoNuevo.hora_ingreso,
        "hora_cierre": eventoNuevo.hora_cierre,
        "id_club": {
          "idClub": sessionStorage.getItem("id_user")
        }
      };

      APIInvoke.invokePOST("api/Evento/", data);

      swal({
        title: "Evento creado",
        text: "El evento ha sido creado satisfactoriamente",
        icon: "success",
        buttons: {
          confirm: {
            text: "Ok",
            value: true,
            visible: true,
            className: "btn btn-success",
            closeModal: true,
          },
        },
      })
        .then((value) => {
          if (value) {
            
          }
        });

    } catch (error) {
      console.error("Error al publicar el evento", error);
      alert("Ha ocurrido un error, intente m치s tarde");
    }

  };

  const a침adirLocalidad = () => {
    if (!oponente || !fecha || !hora_ingreso || !hora_cierre || !estadio || localities.length === 0) {
      alert('Por favor, completa todos los campos y a침ade al menos una localidad.');
      return;
    }
    onCreateEvent({ oponente, fecha, hora_ingreso, hora_cierre, estadio, localities });

    setLocalities([]);
  }

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Oponente del partido:</label>
        <input
          type="text"
          value={oponente}
          name='oponente'
          onChange={setEvento}
          required />
      </div>
      <div>
        <label>Fecha del Evento:</label>
        <input
          type="date"
          value={fecha}
          name='fecha'
          onChange={setEvento}
          required />
      </div>
      <div>
        <label>Hora de Ingreso:</label>
        <input
          type="time"
          value={hora_ingreso}
          name='hora_ingreso'
          onChange={setEvento}
          required />
      </div>
      <div>
        <label>Hora de Salida:</label>
        <input
          type="time"
          value={hora_cierre}
          name='hora_cierre'
          onChange={setEvento}
          required />
      </div>
      <div>
        <label>Ubicaci칩n del Evento - Estadio:</label>
        <input
          type="text"
          value={estadio}
          name='estadio'
          onChange={setEvento}
          required />
      </div>
      <div>
        <label>Nombre de la Localidad:</label>
        <input
          type="text"
          value={localityName}
          onChange={(e) => setLocalityName(e.target.value)} />
      </div>
      <div>
        <label>Capacidad de la Localidad:</label>
        <input
          type="number"
          value={localityCapacity}
          onChange={(e) => setLocalityCapacity(e.target.value)} />
      </div>
      <button type="button" className='btn btn-info' onClick={handleAddLocality}>A침adir Localidad</button>
      <ul>
        {localities.map((locality, index) => (
          <li key={index}>{locality.name} - {locality.capacity} personas</li>
        ))}
      </ul>
      <button type="submit" className='btn btn-success'>Crear Evento</button>
    </form>
  );
}

export default CreateEventForm;
