// src/components/LoginForm.js
import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';

function LoginForm({ setUserType }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const history = useHistory();

  const handleSubmit = (e) => {
    e.preventDefault();
    // Validación simple de usuario y contraseña
    if (username === 'club' && password === 'password') {
      setUserType('club');
      history.push('/club/dashboard');
    } else if (username === 'seguidor' && password === 'password') {
      setUserType('seguidor');
      history.push('/seguidor/dashboard');
    } else {
      alert('Usuario o contraseña incorrectos');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Login</h2>
      <label>
        Usuario:
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
      </label>
      <br />
      <label>
        Contraseña:
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
      </label>
      <br />
      <button type="submit">Iniciar Sesión</button>
    </form>
  );
}

export default LoginForm;
