// src/components/CreateEventForm.js
import React, { useState } from 'react';

function CreateEventForm({ onCreateEvent }) {
  const [name, setName] = useState('');
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  const [location, setLocation] = useState('');
  const [localities, setLocalities] = useState([]);
  const [localityName, setLocalityName] = useState('');
  const [capacity, setCapacity] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validación de los campos
    if (!name || !date || !time || !location) {
      alert('Por favor, completa todos los campos del evento.');
      return;
    }

    if (localities.length === 0) {
      alert('Por favor, añade al menos una localidad.');
      return;
    }

    onCreateEvent({ name, date, time, location, localities });
    setName('');
    setDate('');
    setTime('');
    setLocation('');
    setLocalities([]);
  };

  const addLocality = () => {
    // Validación de los campos de la localidad
    if (!localityName || !capacity) {
      alert('Por favor, completa todos los campos de la localidad.');
      return;
    }

    setLocalities([...localities, { name: localityName, capacity: parseInt(capacity, 10), soldTickets: 0 }]);
    setLocalityName('');
    setCapacity('');
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Nombre del Evento:</label>
        <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
      </div>
      <div>
        <label>Fecha del Evento:</label>
        <input type="date" value={date} onChange={(e) => setDate(e.target.value)} required />
      </div>
      <div>
        <label>Hora del Evento:</label>
        <input type="time" value={time} onChange={(e) => setTime(e.target.value)} required />
      </div>
      <div>
        <label>Ubicación del Evento:</label>
        <input type="text" value={location} onChange={(e) => setLocation(e.target.value)} required />
      </div>
      <div>
        <label>Localidades:</label>
        <input type="text" placeholder="Nombre de la Localidad" value={localityName} onChange={(e) => setLocalityName(e.target.value)} />
        <input type="number" placeholder="Capacidad" value={capacity} onChange={(e) => setCapacity(e.target.value)} />
        <button type="button" onClick={addLocality}>Añadir Localidad</button>
      </div>
      <div>
        <h3>Localidades Creadas:</h3>
        <ul>
          {localities.map((locality, index) => (
            <li key={index}>{locality.name} - Capacidad: {locality.capacity}</li>
          ))}
        </ul>
      </div>
      <button type="submit">Crear Evento</button>
    </form>
  );
}

export default CreateEventForm;
