// src/pages/SeguidorDashboard.js
import React, { useState } from 'react';

function SeguidorDashboard({ events }) {
  // Estado para manejar la visualización de boletas y servicios adicionales
  const [showTickets, setShowTickets] = useState(false);
  const [showServices, setShowServices] = useState(false);

  // Función para comprar boletas
  const handleBuyTickets = (eventId) => {
    // Lógica para comprar boletas
    console.log(`Comprar boletas para el evento con ID: ${eventId}`);
    // Aquí puedes implementar la lógica para la compra de boletas
    alert(`Boletas compradas para el evento con ID: ${eventId}`);
  };

  // Función para ver servicios adicionales
  const handleViewServices = (eventId) => {
    // Lógica para ver servicios adicionales
    console.log(`Ver servicios adicionales para el evento con ID: ${eventId}`);
    // Aquí puedes implementar la lógica para mostrar los servicios adicionales disponibles
    setShowServices(true); // Mostrar servicios adicionales
  };

  // Función para ocultar servicios adicionales
  const handleCloseServices = () => {
    setShowServices(false);
  };

  return (
    <div className="dashboard">
      <h1>Seguidor Dashboard</h1>

      {/* Lista de eventos */}
      <div className="event-list">
        <h2>Eventos Disponibles</h2>
        <ul>
          {events.map((event) => (
            <li key={event.id}>
              <strong>Nombre:</strong> {event.name} <br />
              <strong>Fecha:</strong> {event.date} <br />
              <strong>Ubicación:</strong> {event.location} <br />
              {/* Botón para comprar boletas */}
              <button onClick={() => handleBuyTickets(event.id)}>Comprar Boletas</button>
              {/* Botón para ver servicios adicionales */}
              <button onClick={() => handleViewServices(event.id)}>Ver Servicios Adicionales</button>
              {/* Mostrar servicios adicionales */}
              {showServices && (
                <div>
                  <h3>Servicios Adicionales</h3>
                  <ul>
                    {/* Ejemplo de servicios adicionales */}
                    <li>Servicio 1</li>
                    <li>Servicio 2</li>
                    <li>Servicio 3</li>
                  </ul>
                  <button onClick={handleCloseServices}>Cerrar Servicios</button>
                </div>
              )}
            </li>
          ))}
        </ul>
      </div>

      {/* Sección para ver boletas compradas */}
      <div className="ticket-section">
        <h2>Boletas Compradas</h2>
        <p>Implementar aquí la lista de boletas compradas por el seguidor.</p>
        {/* Aquí se mostrarán las boletas compradas */}
      </div>
    </div>
  );
}

export default SeguidorDashboard;
