import React, { useState } from 'react';
import { useNavigate } from "react-router-dom";
import Navbar from '../components/Navbar';

const RegistroForm = () => {
    const navigate = useNavigate();

    const [usuario, setUsuario] = useState({
        email: "",
        passwordUser: "",
        nameUser: "",
    });

    const { nameUser, email, passwordUser } = usuario;

    const onChange = (e) => {
        setUsuario({
            ...usuario,
            [e.target.name]: e.target.value,
        });
    };

    const register = async () => {

        const data = {
            nameUser: usuario.nameUser,
            email: usuario.email,
            passwordUser: usuario.passwordUser,
        };
        const response = await APIInvoke.invokePOST(`api/Users/`, data);
        const mensaje = response.msg;

        if (mensaje === "El usuario ya existe") {
            const msg = "El usuario ya existe.";
            swal({
                title: "Error",
                text: msg,
                icon: "error",
                buttons: {
                    confirm: {
                        text: "Ok",
                        value: true,
                        visible: true,
                        className: "btn btn-danger",
                        closeModal: true,
                    },
                },
            });
        } else {
            const msg = "El usuario fue creado correctamente.";
            sessionStorage.setItem("id_user", response.id);
            sessionStorage.setItem("name_user", response.nameUser);
            navigate("/");
            swal({
                title: "Información",
                text: msg,
                icon: "success",
                buttons: {
                    confirm: {
                        text: "Ok",
                        value: true,
                        visible: true,
                        className: "btn btn-primary",
                        closeModal: true,
                    },
                },
            });

            setUsuario({
                email: "",
                passwordUser: "",
                confirmPassword: "",
                nameUser: "",
            });
        }
    };

    const onSubmit = (e) => {
        e.preventDefault();
        register();
    };

    const [showPassword, setShowPassword] = useState(false);

    return (
        <div>
            <Navbar />
            <div
                className="container mt-5 p-5 mb-5"
                style={{ backgroundColor: "rgb(204, 204, 204)", borderRadius: 15 }}
            >
                <div className="text-center">
                    <h3>Regístrate</h3>
                </div>

                <form onSubmit={onSubmit}>
                    <div className="input-field mb-3">
                        <label htmlFor="nombreUsuario" className="form-label">
                            Nombre de usuario
                        </label>
                        <input
                            type="text"
                            className="form-control"
                            placeholder="NombreUsuario"
                            id="nameUser"
                            name="nameUser"
                            value={nameUser}
                            onChange={onChange}
                            required
                        />
                    </div>
                    <div className="input-field mb-3">
                        <label htmlFor="email" className="form-label">
                            Correo electrónico
                        </label>
                        <input
                            type="email"
                            className="form-control"
                            placeholder="correo@example.com"
                            id="email"
                            name="email"
                            value={email}
                            onChange={onChange}
                            required
                        />
                    </div>
                    <div className="input-field mb-3">
                        <label htmlFor="password" className="form-label">
                            Contraseña
                        </label>
                        <div className="password-wrapper">
                            <input
                                type={showPassword ? "text" : "password"}
                                id="passwordUser"
                                name="passwordUser"
                                className="form-control"
                                placeholder="************"
                                value={passwordUser}
                                onChange={onChange}
                                required
                            />
                        </div>
                    </div>
                    <button
                        className="btn btn-block btn-success text-center"
                        type="submit"
                    >
                        Registrarme
                    </button>
                </form>
            </div>
        </div>
    )
};

export default RegistroForm;
