// src/components/LoginForm.js
import React, { useState } from 'react';
import { useNavigate } from "react-router-dom";

function LoginForm({ onLogin }) {
  const navigate = useNavigate();

  const [userType, setUserType] = useState('club');

  const [usuario, setUsuario] = useState({
    correo_electronico: "",
    contrasena: "",
  });

  const { correo_electronico, contrasena } = usuario;

  const setUser = (e) => {
    setUsuario({
      ...usuario,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!correo_electronico || !contrasena) {
      alert('Por favor, complete todos los campos.');
      return;
    }

    const data = {
      correo_electronico: usuario.correo_electronico,
      contrasena: usuario.contrasena,
    };

    if(userType === "club"){
      console.log("esto es un club")
      console.log(data)
    }else{
      console.log("esto es un seguidor")
      navigate("/seguidor")
    }
    onLogin(userType);
  };

  return (
    <form onSubmit={handleSubmit} className="form">
      <h2>Login</h2> {/* Título agregado */}
      <div>
        <label>Correo electronico:</label>
        <input
          type="text"
          name='correo_electronico'
          value={correo_electronico}
          onChange={setUser}
          required
        />
      </div>
      <div>
        <label>Contraseña:</label>
        <input
          type="password"
          name='contrasena'
          value={contrasena}
          onChange={setUser}
          required
        />
      </div>
      <div>
        <label>Tipo de Usuario:</label>
        <select
          value={userType}
          onChange={(e) => setUserType(e.target.value)}
        >
          <option value="club">Club</option>
          <option value="seguidor">Seguidor</option>
        </select>
      </div>
      <button type="submit">Login</button>
    </form>
  );
}

export default LoginForm;
