// src/components/ClubDashboard.js
import React, { useState } from 'react';

function ClubDashboard({ events, onCreateEvent, onLinkFollower, onNotifyFollowers }) {
  const [isCreateEventOpen, setCreateEventOpen] = useState(false);
  const [isLinkFollowerOpen, setLinkFollowerOpen] = useState(false);
  const [isNotifyFollowersOpen, setNotifyFollowersOpen] = useState(false);
  const [eventName, setEventName] = useState('');
  const [eventDate, setEventDate] = useState('');
  const [eventLocation, setEventLocation] = useState('');
  const [eventTime, setEventTime] = useState('');
  const [eventLocations, setEventLocations] = useState([]);

  const handleToggleCreateEvent = () => {
    setCreateEventOpen(!isCreateEventOpen);
    setLinkFollowerOpen(false);
    setNotifyFollowersOpen(false);
  };

  const handleToggleLinkFollower = () => {
    setLinkFollowerOpen(!isLinkFollowerOpen);
    setCreateEventOpen(false);
    setNotifyFollowersOpen(false);
  };

  const handleToggleNotifyFollowers = () => {
    setNotifyFollowersOpen(!isNotifyFollowersOpen);
    setCreateEventOpen(false);
    setLinkFollowerOpen(false);
  };

  const handleCreateEvent = (event) => {
    event.preventDefault();
    if (!eventName || !eventDate || !eventLocation || !eventTime) {
      alert('Por favor completa todos los campos.');
      return;
    }

    const newEvent = {
      name: eventName,
      date: eventDate,
      location: eventLocation,
      time: eventTime,
      locations: eventLocations,
    };

    onCreateEvent(newEvent);

    // Limpiar campos después de guardar el evento
    setEventName('');
    setEventDate('');
    setEventLocation('');
    setEventTime('');
    setEventLocations([]);

    setCreateEventOpen(false); // Cerrar el formulario después de guardar el evento
  };

  const handleAddLocation = () => {
    if (!eventLocation || !eventTime) {
      alert('Por favor completa el nombre y la capacidad de la localidad.');
      return;
    }

    const newLocation = {
      name: eventLocation,
      capacity: eventTime,
    };

    setEventLocations([...eventLocations, newLocation]);
    setEventLocation('');
    setEventTime('');
  };

  const handleLinkFollower = (followerData) => {
    onLinkFollower(followerData);
    setLinkFollowerOpen(false); // Cerrar el formulario después de vincular al seguidor
  };

  const handleNotifyFollowers = (message) => {
    if (events.length > 0) {
      onNotifyFollowers(message);
      setNotifyFollowersOpen(false); // Cerrar el formulario después de notificar a los seguidores
    } else {
      alert('No se puede enviar el mensaje, no hay eventos creados');
    }
  };

  return (
    <div className="dashboard">
      <h1>Club Dashboard</h1>
      <div>
        <button onClick={handleToggleCreateEvent}>Crear Evento Deportivo</button>
        {isCreateEventOpen && (
          <div className="form">
            <h2>Crear Evento Deportivo</h2>
            <form onSubmit={handleCreateEvent}>
              <input
                type="text"
                placeholder="Nombre del evento"
                value={eventName}
                onChange={(e) => setEventName(e.target.value)}
                required
              />
              <input
                type="date"
                value={eventDate}
                onChange={(e) => setEventDate(e.target.value)}
                required
              />
              <input
                type="text"
                placeholder="Ubicación del evento"
                value={eventLocation}
                onChange={(e) => setEventLocation(e.target.value)}
                required
              />
              <input
                type="time"
                value={eventTime}
                onChange={(e) => setEventTime(e.target.value)}
                required
              />
              <button type="button" onClick={handleAddLocation}>
                Añadir Localidad
              </button>
              <ul>
                {eventLocations.map((loc, index) => (
                  <li key={index}>
                    {loc.name} - Capacidad: {loc.capacity}
                  </li>
                ))}
              </ul>
              <button type="submit">Guardar Evento</button>
            </form>
          </div>
        )}
      </div>
      <div>
        <button onClick={handleToggleLinkFollower}>Vincular Seguidor</button>
        {isLinkFollowerOpen && (
          <div className="form">
            <h2>Vincular Seguidor</h2>
            <form onSubmit={handleLinkFollower}>
              <input type="email" placeholder="Correo electrónico" required />
              <button type="submit">Vincular Seguidor</button>
            </form>
          </div>
        )}
      </div>
      <div>
        <button onClick={handleToggleNotifyFollowers}>Notificar Seguidores</button>
        {isNotifyFollowersOpen && (
          <div className="form">
            <h2>Notificar Seguidores</h2>
            <form onSubmit={handleNotifyFollowers}>
              <textarea placeholder="Mensaje a enviar" required></textarea>
              <button type="submit">Enviar Mensaje</button>
            </form>
          </div>
        )}
      </div>
    </div>
  );
}

export default ClubDashboard;
