// src/pages/SeguidorDashboard.js
import React, { useState } from 'react';

function SeguidorDashboard({ events }) {
  // Estado para manejar la visualización de boletas y servicios adicionales
  const [showTickets, setShowTickets] = useState(false);
  const [showServices, setShowServices] = useState(false);
  const [tickets, setTickets] = useState([
    { id: 1, eventName: 'Partido de Fútbol', locality: 'General', quantity: 2 },
  ]);

  // Función para comprar boletas
  const handleBuyTickets = (eventId) => {
    // Lógica para comprar boletas (ejemplo)
    const event = events.find(event => event.id === eventId);
    const newTicket = {
      id: tickets.length + 1,
      eventName: event.name,
      locality: 'Preferencial',
      quantity: 1
    };
    setTickets([...tickets, newTicket]);
    alert(`Boleta comprada para el evento: ${event.name}`);
  };

  // Función para transferir boletas
  const handleTransferTickets = (ticketId, newOwnerEmail) => {
    // Lógica para transferir boletas (ejemplo)
    const ticketToTransfer = tickets.find(ticket => ticket.id === ticketId);
    alert(`Boleta transferida para el evento: ${ticketToTransfer.eventName} a ${newOwnerEmail}`);
    // Aquí se podría implementar la lógica real de transferencia
  };

  // Función para vender boletas en mercado secundario
  const handleSellTicket = (ticketId) => {
    // Lógica para vender boletas en mercado secundario (ejemplo)
    const ticketToSell = tickets.find(ticket => ticket.id === ticketId);
    alert(`Boleta vendida en mercado secundario para el evento: ${ticketToSell.eventName}`);
    // Aquí se podría implementar la lógica real de venta en mercado secundario
  };

  return (
    <div className="dashboard">
      <h1>Seguidor Dashboard</h1>

      {/* Mostrar boletas compradas */}
      <div>
        <button onClick={() => setShowTickets(!showTickets)}>
          {showTickets ? 'Ocultar Boletas' : 'Ver Boletas Compradas'}
        </button>
        {showTickets && (
          <div className="ticket-list">
            <h2>Boletas Compradas</h2>
            <ul>
              {tickets.map(ticket => (
                <li key={ticket.id}>
                  <strong>Evento:</strong> {ticket.eventName} <br />
                  <strong>Localidad:</strong> {ticket.locality} <br />
                  <strong>Cantidad:</strong> {ticket.quantity} <br />
                  <button onClick={() => handleTransferTickets(ticket.id, 'nuevo@email.com')}>
                    Transferir Boleta
                  </button>
                  <button onClick={() => handleSellTicket(ticket.id)}>
                    Vender Boleta en Mercado Secundario
                  </button>
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>

      {/* Funcionalidad adicional para servicios */}
      <div>
        <button onClick={() => setShowServices(!showServices)}>
          {showServices ? 'Ocultar Servicios Adicionales' : 'Ver Servicios Adicionales'}
        </button>
        {showServices && (
          <div className="service-list">
            <h2>Servicios Adicionales</h2>
            <p>Funcionalidad para ver servicios adicionales aquí...</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default SeguidorDashboard;
