// src/components/ClubDashboard.js
import React, { useState } from 'react';
import CreateEventForm from './CreateEventForm';

function ClubDashboard({ events, onCreateEvent, onLinkFollower, onNotifyFollowers }) {
  const [showCreateEventForm, setShowCreateEventForm] = useState(false);
  const [showLinkFollowerForm, setShowLinkFollowerForm] = useState(false);
  const [showNotifyFollowersForm, setShowNotifyFollowersForm] = useState(false);

  return (
    <div>
      <h1>Club Dashboard</h1>
      <button onClick={() => setShowCreateEventForm(!showCreateEventForm)}>Crear Evento Deportivo</button>
      <button onClick={() => setShowLinkFollowerForm(!showLinkFollowerForm)}>Vincular Seguidor</button>
      <button onClick={() => setShowNotifyFollowersForm(!showNotifyFollowersForm)}>Notificar Seguidores</button>

      {showCreateEventForm && <CreateEventForm onCreateEvent={onCreateEvent} />}
      {showLinkFollowerForm && <LinkFollowerForm onLinkFollower={onLinkFollower} />}
      {showNotifyFollowersForm && <NotifyFollowersForm onNotifyFollowers={onNotifyFollowers} events={events} />}

      <h2>Eventos Creados:</h2>
      <ul>
        {events.map((event, index) => (
          <li key={index}>
            {event.name} - {event.date} - {event.time} - {event.location}
            <button onClick={() => console.log(`Consultar boletas vendidas para el evento ${event.name}`)}>Consultar Boletas Vendidas</button>
            <button onClick={() => console.log(`Crear servicio adicional para el evento ${event.name}`)}>Crear Servicio Adicional</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

function LinkFollowerForm({ onLinkFollower }) {
  const [email, setEmail] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!email) {
      alert('Por favor, introduce un correo electrónico.');
      return;
    }
    onLinkFollower(email);
    setEmail('');
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Correo Electrónico del Seguidor:</label>
        <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
      </div>
      <button type="submit">Vincular Seguidor</button>
    </form>
  );
}

function NotifyFollowersForm({ onNotifyFollowers, events }) {
  const [message, setMessage] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    onNotifyFollowers(message);
    setMessage('');
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Mensaje:</label>
        <textarea value={message} onChange={(e) => setMessage(e.target.value)} required />
      </div>
      <button type="submit">Enviar Mensaje</button>
    </form>
  );
}

export default ClubDashboard;
