// src/App.js
import React, { useState } from 'react';
import LoginForm from './components/LoginForm';
import CreateEventForm from './components/CreateEventForm';
import './App.css';

function App() {
  const [loggedIn, setLoggedIn] = useState(false);
  const [userType, setUserType] = useState('');
  const [events, setEvents] = useState([]);
  const [showCreateEventForm, setShowCreateEventForm] = useState(false);
  const [selectedEvent, setSelectedEvent] = useState(null);

  const handleLogin = (username, password, type) => {
    setLoggedIn(true);
    setUserType(type);
  };

  const handleCreateEvent = (newEvent) => {
    setEvents([...events, newEvent]);
    console.log('Nuevo evento creado:', newEvent);
    setShowCreateEventForm(false);
  };

  const showCreateEventFormHandler = () => {
    setShowCreateEventForm(true);
  };

  const handleShowTicketsSold = (event) => {
    setSelectedEvent(event);
  };

  return (
    <div className="App">
      {!loggedIn ? (
        <LoginForm handleLogin={handleLogin} />
      ) : (
        <div>
          <h1>Bienvenido {userType === 'club' ? 'Club' : 'Seguidor'}</h1>
          {userType === 'club' && (
            <div>
              <button onClick={showCreateEventFormHandler}>Crear Evento Deportivo</button>
              <button onClick={() => alert('Vincular seguidor')}>Vincular Seguidor</button>
              <button onClick={() => alert('Notificar seguidores')}>Notificar Seguidores</button>
              {showCreateEventForm && <CreateEventForm onCreateEvent={handleCreateEvent} />}
              <h2>Eventos Creados</h2>
              <ul>
                {events.length > 0 ? (
                  events.map((event, index) => (
                    <li key={index}>
                      {event.name} - {event.date} - {event.time} - {event.location}
                      <button onClick={() => handleShowTicketsSold(event)}>Consultar Boletas Vendidas</button>
                      <button onClick={() => alert(`Crear servicio adicional para ${event.name}`)}>Crear Servicio Adicional</button>
                    </li>
                  ))
                ) : (
                  <li>No hay eventos creados</li>
                )}
              </ul>
              {selectedEvent && (
                <div>
                  <h3>Boletas Vendidas para {selectedEvent.name}</h3>
                  <ul>
                    {selectedEvent.localities.map((locality, index) => (
                      <li key={index}>
                        {locality.name} - Capacidad: {locality.capacity} - Boletas Vendidas: {locality.soldTickets}
                      </li>
                    ))}
                  </ul>
                  <button onClick={() => setSelectedEvent(null)}>Cerrar</button>
                </div>
              )}
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
